using System; namespace vezba { class Program { static void Main(string[] args) { float x, y; string op; Console.Write("Input X: "); x = float.Parse(Console.ReadLine()); Console.WriteLine(); Console.Write("Imput Y: "); y = float.Parse(Console.ReadLine()); Console.WriteLine(); Console.Write("Please choose operator: "); op = Console.ReadLine(); switch (op) { case "+": Console.WriteLine($"X+Y={x + y}"); break; case "-": Console.WriteLine($"X-Y={x - y}"); break; case "*": Console.WriteLine($"X*Y={x * y}"); break; case "/": Console.WriteLine($"X/Y={x / y}"); break; case "": Console.WriteLine("Tebra moras da upises nesto"); break; default: Console.WriteLine("Decak imas defekat"); break; } } } }